sp_widget.client_script:
function () {
  /* widget controller */
  var c = this;
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	obj={};
	data.list=[];
	alamCL=[];
	alamRS='';
	system=$sp.getParameter('system');
	// First Get Alarm info

	for (var i=0;i<2;i++) {
		 alamCL.push(parseInt(getCount(i,system)));
	}
	alamRS=alamCL[0].toString();
	obj.unit='Alarm Count Today';
	var avg=average(alamCL);
	var v=standardDeviation(alamCL).toFixed(0)
	var cv=(v/avg*100).toFixed(1);
	alamRS=alamRS+' (RSD for 2 days : '+cv+') LIMIT at 250'
	alamCL.push(cv);
	alamCL.push(avg)
	alamCL.push(v);
	obj.target=alamRS;
	obj.value='true';
	data.list.push(obj);
	obj={};		

	// Get status infos
	var getStatusInf=new GlideRecord('x_agsa_promonitor_last_metrics');
	getStatusInf.addEncodedQuery('value=true^ORvalue=false');
	getStatusInf.addQuery('sid',system);
	getStatusInf.query();
	while (getStatusInf.next()) {
		obj.unit=getStatusInf.unit.toString();
		obj.target=getStatusInf.target.toString();
		obj.value=getStatusInf.value.toString();
		data.list.push(obj);
		obj={};
	}


	/// 
		function standardDeviation(values){
		var avg = average(values);		
		var squareDiffs = values.map(function(value){
			var diff = value - avg;
			var sqrDiff = diff * diff;
			return sqrDiff;
		});
		var avgSquareDiff = average(squareDiffs);
		var stdDev = Math.sqrt(avgSquareDiff);
		return stdDev;
	}

	function average(data){
		var sum = data.reduce(function(sum, value){
			return sum + value;
		}, 0);
		var avg = sum / data.length;
		return avg;
	}

	function getCount(days,sid) {
		var count = new GlideAggregate('x_agsa_promonitor_alarms');
		if (sid != '') {
				count.addQuery('sid.sid',sid);
		}
		count.addQuery('sys_updated_on', '>', gs.daysAgoStart(days));
		count.addQuery('sys_updated_on', '<', gs.daysAgoEnd(days));
		count.addQuery('acknowledge','false');
		count.addAggregate('COUNT');
		count.query();
		count.next();
		return count.getAggregate('COUNT');
	}
})();
